mapper.select:
SELECT t_spu_attrs.*
FROM t_spu_attrs t_spu_attrs


        AND ( INSTR(t_spu_attrs.name,#{queryForm.keywords})
        )



        and t_spu_attrs.is_deleted = #{queryForm.isDeleted}



        and t_spu_attrs.is_disabled = #{queryForm.isDisabled}

SELECT t_spu_attrs.*
FROM t_spu_attrs t_spu_attrs



        and t_spu_attrs.is_deleted = #{queryForm.isDeleted}



        and t_spu_attrs.is_disabled = #{queryForm.isDisabled}

mapper.update:
UPDATE t_spu_attrs
SET is_disabled = #{isDisabled}
WHERE id = #{id}
